		if($data['Book_Title'] == empty_lookup_value){ $data['Book_Title'] = ''; }
	$data['Book_Type'] = makeSafe($_REQUEST['Book_Type']);
		if($data['Book_Type'] == empty_lookup_value){ $data['Book_Type'] = ''; }
	$data['Author_Name'] = makeSafe($_REQUEST['Author_Name']);
		if($data['Author_Name'] == empty_lookup_value){ $data['Author_Name'] = ''; }
	$data['Quantity'] = makeSafe($_REQUEST['Quantity']);
		if($data['Quantity'] == empty_lookup_value){ $data['Quantity'] = ''; }
	$data['Purchase_Date'] = intval($_REQUEST['Purchase_DateYear']) . '-' . intval($_REQUEST['Purchase_DateMonth']) . '-' . intval($_REQUEST['Purchase_DateDay']);
	$data['Purchase_Date'] = parseMySQLDate($data['Purchase_Date'], '1');
	$data['Edition'] = makeSafe($_REQUEST['Edition']);
		if($data['Edition'] == empty_lookup_value){ $data['Edition'] = ''; }
	$data['Price'] = makeSafe($_REQUEST['Price']);
		if($data['Price'] == empty_lookup_value){ $data['Price'] = ''; }
	$data['Pages'] = makeSafe($_REQUEST['Pages']);
		if($data['Pages'] == empty_lookup_value){ $data['Pages'] = ''; }
	$data['Publisher'] = makeSafe($_REQUEST['Publisher']);
		if($data['Publisher'] == empty_lookup_value){ $data['Publisher'] = ''; }
	$data['selectedID']=makeSafe($selected_id);

	// hook: books_before_update
	if(function_exists('books_before_update')){
		$args=array();
		if(!books_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `books` set       `ISBN_NO`=' . (($data['ISBN_NO'] !== '' && $data['ISBN_NO'] !== NULL) ? "'{$data['ISBN_NO']}'" : 'NULL') . ', `Book_Title`=' . (($data['Book_Title'] !== '' && $data['Book_Title'] !== NULL) ? "'{$data['Book_Title']}'" : 'NULL') . ', `Book_Type`=' . (($data['Book_Type'] !== '' && $data['Book_Type'] !== NULL) ? "'{$data['Book_Type']}'" : 'NULL') . ', `Author_Name`=' . (($data['Author_Name'] !== '' && $data['Author_Name'] !== NULL) ? "'{$data['Author_Name']}'" : 'NULL') . ', `Quantity`=' . (($data['Quantity'] !== '' && $data['Quantity'] !== NULL) ? "'{$data['Quantity']}'" : 'NULL') . ', `Purchase_Date`=' . (($data['Purchase_Date'] !== '' && $data['Purchase_Date'] !== NULL) ? "'{$data['Purchase_Date']}'" : 'NULL') . ', `Edition`=' . (($data['Edition'] !== '' && $data['Edition'] !== NULL) ? "'{$data['Edition']}'" : 'NULL') . ', `Price`=' . (($data['Price'] !== '' && $data['Price'] !== NULL) ? "'{$data['Price']}'" : 'NULL') . ', `Pages`=' . (($data['Pages'] !== '' && $data['Pages'] !== NULL) ? "'{$data['Pages']}'" : 'NULL') . ', `Publisher`=' . (($data['Publisher'] !== '' && $data['Publisher'] !== NULL) ? "'{$data['Publisher']}'" : 'NULL') . " where `id`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="books_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: books_after_update
	if(function_exists('books_after_update')){
		$res = sql("SELECT * FROM `books` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id'];
		$args = array();
		if(!books_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='books' and pkValue='".makeSafe($selected_id)."'", $eo);

}

