		$jsReadOnly .= "\tjQuery('#Book_Type_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#Author_Name').replaceWith('<div class=\"form-control-static\" id=\"Author_Name\">' + (jQuery('#Author_Name').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#Quantity').replaceWith('<div class=\"form-control-static\" id=\"Quantity\">' + (jQuery('#Quantity').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#Purchase_Date').prop('readonly', true);\n";
		$jsReadOnly .= "\tjQuery('#Purchase_DateDay, #Purchase_DateMonth, #Purchase_DateYear').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#Edition').replaceWith('<div class=\"form-control-static\" id=\"Edition\">' + (jQuery('#Edition').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#Price').replaceWith('<div class=\"form-control-static\" id=\"Price\">' + (jQuery('#Price').val() || '') + '</div>');\n";
