		if( $dvprint) $templateCode = str_replace('<%%VALUE(Book_Title)%%>', safe_html($urow['Book_Title']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Book_Title)%%>', html_attr($row['Book_Title']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Book_Title)%%>', urlencode($urow['Book_Title']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Book_Type)%%>', safe_html($urow['Book_Type']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Book_Type)%%>', html_attr($row['Book_Type']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Book_Type)%%>', urlencode($urow['Book_Type']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Author_Name)%%>', safe_html($urow['Author_Name']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Author_Name)%%>', html_attr($row['Author_Name']), $templateCode);
