		$templateCode = str_replace('<%%URLVALUE(Quantity)%%>', urlencode($urow['Quantity']), $templateCode);
		$templateCode = str_replace('<%%VALUE(Purchase_Date)%%>', @date('m/d/Y', @strtotime(html_attr($row['Purchase_Date']))), $templateCode);
