		if( $dvprint) $templateCode = str_replace('<%%VALUE(Edition)%%>', safe_html($urow['Edition']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Edition)%%>', html_attr($row['Edition']), $templateCode);
