		if( $dvprint) $templateCode = str_replace('<%%VALUE(Pages)%%>', safe_html($urow['Pages']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Pages)%%>', html_attr($row['Pages']), $templateCode);
