		$templateCode = str_replace('<%%VALUE(Book_Title)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Book_Title)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Book_Type)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Book_Type)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Author_Name)%%>', '', $templateCode);
